kmsq.MSE <- function(WETA_df, rad_m, graph = FALSE, filter = TRUE, enforce_fp = TRUE, mse = TRUE){
  # rad_m <- sqrt(rad_m)
  
  if(filter){
    checklists_filtered <- filter_repeat_visits(
      WETA_df,
      min_obs = MIN_OBS,
      max_obs = MAX_OBS,
      annual_closure = TRUE,
      date_var = "formatted_date",
      site_vars = c("locality_id")
    )
    checklists_filtered <- subset(checklists_filtered, select = -c(site, n_observations, closure_id))
  } else {
    checklists_filtered <- WETA_df
  }
  
  coords<-data.frame(x = checklists_filtered$longitude, y = checklists_filtered$latitude)
  crs<-"+proj=longlat +datum=WGS84 +no_defs +ellps=WGS84 +towgs84=0,0,0"
  
  WETA.spdf <- SpatialPointsDataFrame(coords= coords, data = checklists_filtered, proj4string =
                                        CRS(crs))
  
  Albers.crs <-CRS("+proj=aea +lat_1=42 +lat_2=48 +lon_0=-122 +x_0=0 +y_0=0 +ellps=GRS80 +towgs84=0,0,0,0,0,0,0 +units=m +no_defs")
  WETA.albers <-spTransform(WETA.spdf, CRS=Albers.crs)
  
  x <- seq(from = WETA.albers@bbox["x","min"]-(rad_m*5), to = WETA.albers@bbox["x","max"]+(rad_m*5), by = rad_m)
  y <- seq(from = WETA.albers@bbox["y","min"]-(rad_m*5), to = WETA.albers@bbox["y","max"]+(rad_m*5), by = rad_m)
  xy <- expand.grid(x = x, y = y)
  grid.pts<-SpatialPointsDataFrame(coords= xy, data=xy)
  # grid.pts<-SpatialPointsDataFrame(coords= xy, data=xy, proj4string = CRS(Albers.crs))
  
  gridded(grid.pts) <- TRUE
  
  grid <- as(grid.pts, "SpatialPolygons")
  gridspdf <- SpatialPolygonsDataFrame(grid, data=data.frame(id=row.names(grid),row.names=row.names(grid)))
  
  
  names.grd<-sapply(gridspdf@polygons, function(x) slot(x,"ID"))
  if(graph){
    plot(grid)
    points(WETA.albers, col="red")
  }
  WETA.albers <-spTransform(WETA.spdf, CRS=Albers.crs)
  proj4string(gridspdf) <- Albers.crs
  o = over(WETA.albers, gridspdf)
  new = cbind(WETA.albers@data, o)
  
  WETA.albers@data$site <- as.character(unlist(o))
  
  checklists_df <- WETA.albers@data
  # checklists_df <- enforceClosure(checklists_df, covObj$siteCovs, checklists_df$site)
  if(mse){
    kmSqObj <- calcOccMSE(checklists_df, covObj, TRUE_OCC_COEFF, TRUE_DET_COEFF, syn_spec = TRUE, enforce_false_positives = F, skip_closure = F)
  } else {
    kmSqObj <- checklists_df
  }
  return(kmSqObj)
}



kmsq.Sites <- function(WETA_df, rad_m){
  checklists <- kmsq.MSE(WETA_df, rad_m, graph = FALSE, filter = TRUE, enforce_fp = TRUE, mse = FALSE)
  return(checklists)
}

